mapper.select:
SELECT *
FROM j_system_user        
where su_id = #{su_id} and su_password = #{su_password}

SELECT A.*, ( select B.sc_name from j_system_code B where B.sc_seq = A.su_position)as su_position_name ,( select B.sc_name from j_system_code B where B.sc_seq = A.su_rank)as su_rank_name FROM j_system_user A where A.del_yn='N'
and su_depart = #{su_depart}
and use_yn = #{use_yn}
order by A.su_name asc
